<span class="line"><span style="color:#E06C75;">    </span><span style="color:#E5C07B;">BoxEntity</span><span style="color:#61AFEF;"> map</span><span style="color:#ABB2BF;">(</span><span style="color:#E5C07B;">BoxDto</span><span style="color:#ABB2BF;"> </span><span style="color:#E06C75;font-style:italic;">source</span><span style="color:#ABB2BF;">);</span></span>
